        ".ini", ".cfg", ".config", ".editorconfig", ".manifest", ".master", ".inf", ".env", ".toml", ".dockerfile",
        // šablony
        ".fdf", ".xfdf", ".xsl", ".xslt", ".xsd", ".dtd",
        // c# a Visual Studio
        ".cs", ".csproj", ".xaml", ".resx", ".wsdl", ".sln",
        ".c", ".h", ".cpp", ".hpp", ".vcproj", ".vb", ".vbproj",
        // skriptování a vývoj
        ".bat", ".cmd", ".vbs", ".ps1", ".psm1", ".psd1", ".sh", ".bash", ".zsh", ".reg",
        ".make", ".mk", ".gitignore", ".gitattributes", ".gitmodules",
        ".js", ".ts", ".css", ".less", ".scss", ".sass",
        ".htm", ".html", ".xhtml", ".mht", ".mhtml", ".svg",
